for HOST in $(cat servers.txt )
do
ssh $HOST " hostname && echo -e '------------------------------------\n' && echo "-----------difference two setenv.sh----------\n" && sudo diff /appl/apache-tomcat/bin/setenv.sh /root/jaf_backup/setenv.sh | grep '> ' | sed 's/> //'  && echo "-----------------difference two server.xml--------------\n"  && sudo diff /appl/apache-tomcat/conf/server.xml /root/jaf_backup/server.xml | grep '> ' | sed 's/> //'  &&  echo "-----------------difference two web.xml--------------\n"  && sudo diff /appl/apache-tomcat/conf/web.xml /root/jaf_backup/web.xml  | grep '> ' | sed 's/> //'  && echo "-----------------difference two catalina.properties--------------\n"  && sudo diff /appl/apache-tomcat/conf/catalina.properties /root/jaf_backup/catalina.properties | grep '> ' | sed 's/> //'  && echo "-----------------difference two context.xml--------------\n"  && sudo diff /appl/apache-tomcat/conf/context.xml /root/jaf_backup/context.xml | grep '> ' | sed 's/> //'  && echo "-----------------difference two newrelic.yml--------------\n"  && sudo diff /appl/newrelic/newrelic.yml /root/jaf_backup/newrelic.yml | grep '> ' | sed 's/> //'  && echo "-----------------difference two newrelic-infra.yml--------------\n"  && sudo diff /etc/newrelic-infra.yml /root/jaf_backup/newrelic-infra.yml | grep '> ' | sed 's/> //'  &&  echo -e '---------------------------------------------------\n'  " >> a3.txt
